templates.template:
${:import(ObjectTranslator)}
private var _${word_selection}:Array
		
public function get ${word_selection}():Array 
{
	return _${word_selection};
}

public function set ${word_selection}(value:Array):void 
{
	for (var i:int = 0; i < value.length; i++) 
	{
		value[i] = ObjectTranslator.objectToInstance(value[i], ${cursor});
	}
	_${word_selection} = value;
}

${:import(ObjectTranslator)}
private var _${word_selection}:${class};
		
public function get ${word_selection}():${class} 
{
	return _${word_selection};
}
		
public function set ${word_selection}(value:*):void 
{
	_${word_selection} = ObjectTranslator.objectToInstance(value, ${class});
}
